import React from 'react';

const DetailMovie = ({ movie }) => {

    const createDescMarkup = (description) => { return {__html: description}; };

    return (
        <section>
            <div>
                <div>
                    <h3><strong>Title:</strong> { movie.volumeInfo.title }</h3>
                    <p><strong>Published Date:</strong> {movie.volumeInfo.publishedDate}</p>
                    <p><strong>Publisher:</strong> {movie.volumeInfo.publisher}</p>
                    <p><strong>Page Count:</strong> {movie.volumeInfo.pageCount}</p>
                    <div dangerouslySetInnerHTML={createDescMarkup(movie.volumeInfo.description)} />
                </div>
            </div>
        </section>
    )
};

export default DetailMovie;
