        const API_BASE_URL = `http://localhost:9000/api/movie`;
        const FetchMovie = async () => {
          try {
            const result = await axios.fetch(`${API_BASE_URL}/${movieId}`);
            setMovie(result.json().data);
          } catch (error) {
